import React, { useRef, useState } from "react";
import "./App.css";
import firebase from "firebase/compat/app";
import "firebase/compat/firestore";
import "firebase/compat/auth";
import "firebase/compat/analytics";

import { useAuthState } from "react-firebase-hooks/auth";
import { useCollectionData } from "react-firebase-hooks/firestore";

firebase.initializeApp({
  apiKey: "AIzaSyCHiUlklcQmzX8QACKeVv9_6JB3SAhOBpQ",
  authDomain: "firechat-c6902.firebaseapp.com",
  projectId: "firechat-c6902",
  storageBucket: "firechat-c6902.appspot.com",
  messagingSenderId: "743198984085",
  appId: "1:743198984085:web:87b52605a6bb1e4ca3d89c",
  measurementId: "G-1009D48LWS",
});

const auth = firebase.auth();
const firestore = firebase.firestore();

const App = () => {
  const [user] = useAuthState(auth);

  return (
    <div className="App">
      <header>
        <span>🔥chat</span>
        {user && <SignOut />}
      </header>
      <section>{user ? <ChatRoom /> : <SignIn />}</section>
    </div>
  );
}

const SignIn = () => {
  const signInWithGoogle = () => {
    const provider = new firebase.auth.GoogleAuthProvider();
    auth.signInWithPopup(provider);
  };

  return (
    <div className="signIn">
      <button className="sign-in" onClick={signInWithGoogle}>
        Sign in with Google
      </button>
      <p>
        Do not violate the community guidelines or you will be banned for life!
      </p>
    </div>
  );
}

const SignOut = () => {
  return (
    auth.currentUser && (
      <button className="signOut" onClick={() => auth.signOut()}>
        Sign Out
      </button>
    )
  );
}

const ChatRoom = () => {
  const scrollerRef = useRef();
  const messagesRef = firestore.collection("messages");
  const query = messagesRef.orderBy("createdAt").limit(25);
  const [messages] = useCollectionData(query, { idField: "id" });
  const [formValue, setFormValue] = useState("");

  const sendMessage = async (e) => {
    e.preventDefault();
    const { uid, photoURL } = auth.currentUser;

    await messagesRef.add({
      text: formValue,
      createdAt: firebase.firestore.FieldValue.serverTimestamp(),
      uid,
      photoURL,
    });
    setFormValue("");
    scrollerRef.current.scrollIntoView({ behavior: "smooth" });
  };

  return (
    <>
      <main>
        {messages &&
          messages.map((msg, idx) => (
            <ChatMessage
              key={Math.floor(Math.random() * msg.id + idx)}
              message={msg}
            />
          ))}
        <div ref={scrollerRef} />
      </main>
      <form onSubmit={sendMessage}>
        <input
          value={formValue}
          placeholder={"say something...."}
          onChange={(e) => setFormValue(e.target.value)}
        />
        <button type="submit" disabled={!formValue}>🔥🔥</button>
      </form>
    </>
  );
}

const ChatMessage = ({message}) => {
  const { text, uid, photoURL } = message;
  const messageClass = uid === auth.currentUser.uid ? "sent" : "received";
  return (
    <div className={`message ${messageClass}`}>
      <img src={photoURL} alt={"photo"} />
      <p>{text}</p>
    </div>
  );
}

export default App;
