import React, { useRef, useState } from "react";
import "./App.css";
// import firebase from "firebase/compat/app";
import "firebase/compat/firestore";
import "firebase/compat/auth";
import "firebase/compat/analytics";
import ChatRoom from "./components/ChatRoom";
import authFire from "./firebaseAuth";
import { useAuthState } from "react-firebase-hooks/auth";


// firebase.initializeApp({
//   apiKey: "AIzaSyCHiUlklcQmzX8QACKeVv9_6JB3SAhOBpQ",
//   authDomain: "firechat-c6902.firebaseapp.com",
//   projectId: "firechat-c6902",
//   storageBucket: "firechat-c6902.appspot.com",
//   messagingSenderId: "743198984085",
//   appId: "1:743198984085:web:87b52605a6bb1e4ca3d89c",
//   measurementId: "G-1009D48LWS",
// });

const auth = authFire.firebase.auth();


const App = () => {
  const [user] = useAuthState(auth);

  return (
    <div className="App">
      <header>
        <span>ðŸ”¥chat</span>
        {user && <SignOut />}
      </header>
      <section>{user ? <ChatRoom /> : <SignIn />}</section>
    </div>
  );
}

const SignIn = () => {
  const signInWithGoogle = () => {
    const provider = new firebase.auth.GoogleAuthProvider();
    auth.signInWithPopup(provider);
  };

  return (
    <div className="signIn">
      <button className="sign-in" onClick={signInWithGoogle}>
        Sign in with Google
      </button>
      <p>
        Do not violate the community guidelines or you will be banned for life!
      </p>
    </div>
  );
}

const SignOut = () => {
  return (
    auth.currentUser && (
      <button className="signOut" onClick={() => auth.signOut()}>
        Sign Out
      </button>
    )
  );
}

export default App;
